    <script>
      // Client Side Form Validation
      document.addEventListener("DOMContentLoaded", () => {
        // SELECTORS
        const emailForm = document.querySelector("#email");
        const phoneForm = document.querySelector("#wf-ecom-telephone");
        const submitButton = document.querySelector("#submit-Button");
        const phoneAlerte = document.querySelector("#phoneAlert");
        const emailAlerte = document.querySelector("#emailAlert");
        const reference = document.querySelector("#wf-ecom-notes");
        const referenceAlerte = document.querySelector("#referenceAlert");

        if (!emailForm || !phoneForm || !submitButton || !reference) {
          return;
        }

        // VARIABLES
        let emailIsValid = false;
        let phoneIsValid = false;
        let referenceVide = true;

        function estChampVide(champ) {
          if (champ.trim() === "") {
            return true; // Le champ est vide
          } else {
            return false; // Le champ n'est pas vide
          }
        }

        // HELPER FUNCTIONS
        const validateEmail = (value) => {
          const emailValidation =
            /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/;
          if (value.match(emailValidation)) {
            return true;
          }
          return false;
        };
        const validatePhone = (value) => {
          const phoneValidation = /\d{3}\s?\d{2}\s?\d{3}\s?\d{2}/;
          if (value.match(phoneValidation)) {
            return true;
          }
          return false;
        };
        const applyInvalidStyles = (el) => {
          el.classList.remove("input-valid-state");
          el.classList.add("input-error-state");
        };
        const applyValidStyle = (el) => {
          el.classList.remove("input-error-state");
          el.classList.add("input-valid-state");
        };
        const disableSubmitButton = () => {
          submitButton.disabled = true;
          submitButton.classList.add("submit-disabled-state");
        };
        const enableSubmitButton = () => {
          submitButton.disabled = false;
          submitButton.classList.remove("submit-disabled-state");
        };
        const updateSubmitButton = () => {
          if (emailIsValid && phoneIsValid && !referenceVide) {
            enableSubmitButton();
          } else {
            disableSubmitButton();
          }
        };

        // EVENT LISTENERS
        emailForm.addEventListener("input", (event) => {
          const emailFormValue = event.target.value;
          if (validateEmail(emailFormValue)) {
            emailIsValid = true;
            applyValidStyle(emailForm);
            emailAlerte.style.display = "none";
          } else {
            emailIsValid = false;
            applyInvalidStyles(emailForm);
            emailAlerte.style.display = "block";
          }
          updateSubmitButton();
        });
        phoneForm.addEventListener("input", (event) => {
          const phoneFormValue = event.target.value;
          if (validatePhone(phoneFormValue)) {
            phoneIsValid = true;
            applyValidStyle(phoneForm);
            phoneAlerte.style.display = "none";
          } else {
            phoneIsValid = false;
            applyInvalidStyles(phoneForm);
            phoneAlerte.style.display = "block";
          }
          updateSubmitButton();
        });

        reference.addEventListener("input", (event) => {
          const referenceValue = event.target.value;
          if (estChampVide(referenceValue)) {
            referenceVide = true;
            applyInvalidStyles(reference);
            referenceAlerte.style.display = "block";
          } else {
            referenceVide = false;
            applyValidStyle(reference);
            referenceAlerte.style.display = "none";
          }
          updateSubmitButton();
        });

        //EXECUTION
        updateSubmitButton();
      });
    </script>

    <script>
      //acompte 40%
      let nombreMGA = "";
      async function attendreChargementPrix() {
        return new Promise((resolve, reject) => {
          const elementPrix = document.getElementById("PrixTotal");

          // Vérifie si l'élément existe
          if (elementPrix) {
            // Vérifie si le contenu texte de l'élément est déjà défini
            if (elementPrix.textContent.trim() !== "") {
              resolve(elementPrix.textContent);
            } else {
              // Attends les mutations de l'élément jusqu'à ce que le contenu soit défini
              const observer = new MutationObserver((mutations) => {
                for (const mutation of mutations) {
                  if (
                    mutation.type === "childList" &&
                    mutation.target === elementPrix
                  ) {
                    if (elementPrix.textContent.trim() !== "") {
                      observer.disconnect();
                      resolve(elementPrix.textContent);
                    }
                  }
                }
              });

              // Observe les mutations de l'élément
              observer.observe(elementPrix, { childList: true });
            }
          } else {
            reject(new Error("L'élément PrixTotal n'a pas été trouvé."));
          }
        });
      }

      // Utilisation de await pour attendre le chargement du prix
      (async () => {
        try {
          const prix = await attendreChargementPrix();
          nombreMGA = prix;
        } catch (erreur) {
          console.error(erreur.message);
        }

        // Supprime tout après la virgule
        const prixconvertie = nombreMGA.split(",")[0];

        const nombreEntier = parseInt(prixconvertie.replace(/\s/g, ""), 10); // Supprime les espaces et convertit en entier

        //calcule du 40%
        const acompte = nombreEntier * 0.4;

        //convertir en type double pour afficher 2 zero
        /*
  const variabledouble=acompte.toFixed(2);*/
        const test = document.getElementById("acompteApayer");
        //convertir en type texte pour ajouter un espace
        const texteNombre = acompte.toString();

        // Insère un espace avant le 6e caractère
        const texteAvecEspace =
          texteNombre.slice(0, -3) + " " + texteNombre.slice(-3);
        //afficher dans le front
        test.textContent = texteAvecEspace;
      })();
    </script>
